@import url('https://fonts.googleapis.com/css?family=Roboto');

:root{
    --backgroundColor:#2c3e50;
    --backgroundStop1:#95a5a6;
    --backgroundStop2:#2c3e50;
    --txtbarWidth: 240px;
    --txtbarColor: #95a5a6;
}

*{
    margin: 0;
    padding: 0;
    box-sizing: border-box;
    font-family: 'Roboto', sans-serif;
}

body{
    background-color: #2c3e50;
    background-color: var(--backgroundColor);
    margin: 0;
    padding: 0;
}

.login-box{
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100vh;
    background-image: linear-gradient(45deg,#95a5a6, #2c3e50);
    background-image: linear-gradient(45deg,var(--backgroundStop1), var(--backgroundStop2) );
    transition: 0.4s;

}

.login-form{
    position: absolute;
    top: 40%;
    left: 43%;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
}

.login-form h1{
    color: white;
    font-weight: 400;
}

.txtb{
    width: 240px;
    width: var(--txtbarWidth);
    margin-top: 10px;
    padding: 10px 20px;
    outline: none;
    background: #95a5a6;
    background: var(--txtbarColor);
    border: 1px solid white;
    border-radius: 6px;
    color: white;
    caret-color: #2c3e50;
    text-align: center;
}

.login-btn{
    background: #2c3e50;
    background: var(--backgroundStop2);
    width: 240px;
    width: var(--txtbarWidth);
    border: 0;
    border-radius: 6px;
    margin-top: 10px;
    padding: 10px 20px;
    color: white;
    font-size: 16px;
    cursor: pointer;
}

.hide-login-box {
    color: white;
    position: absolute;
    top: 40px;
    right: 40px;
    opacity: .7;
    cursor: pointer;
    font-size: 24px;
}

.show-login-form{
    color: white;
    position: absolute;
    top: 50%;
    left: 45%;
    border: 2px solid white;
    padding: 10px;
    cursor: pointer;
}

.toggle{
    left: 0;
}
